import React, { useState } from "react"
import { Link } from "react-router-dom"
import {
    Box,
  Button,
  Checkbox,
  FormControl,
  FormControlLabel,
  FilledInput,
  IconButton,
  InputAdornment,
  InputLabel,
  Typography,
  useTheme,
  TextField,
} from "@mui/material"
import { LockOutlined as LockOutlinedIcon } from "@mui/icons-material"
import { tokens } from "../../theme"
import Header from "../../components/Header"
import Visibility from "@mui/icons-material/Visibility"
import VisibilityOff from "@mui/icons-material/VisibilityOff"

const Login = () => {
  const theme = useTheme()
  const colors = tokens(theme.palette.mode)

  const [showPassword, setShowPassword] = useState(false)
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [rememberMe, setRememberMe] = useState(false)
  const [emailError, setEmailError] = useState(false)
  const [passwordError, setPasswordError] = useState(false)

  const handleClickShowPassword = () => setShowPassword((show) => !show)

  const handleMouseDownPassword = (event) => {
    event.preventDefault()
  }

  const handleLogin = () => {
    // Perform login action using email and password values
  }

  const handleRememberMeChange = (event) => {
    setRememberMe(event.target.checked)
    // Save the rememberMe value to local storage or any other storage mechanism
  }

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    return emailRegex.test(email)
  }

  const validatePassword = (password) => {
    const passwordRegex = /^(?=.*[A-Z])(?=.*\d).{6,}$/
    return passwordRegex.test(password)
  }

  const handleEmailChange = (event) => {
    setEmail(event.target.value)
    setEmailError(!validateEmail(event.target.value))
  }

  const handlePasswordChange = (event) => {
    setPassword(event.target.value)
    setPasswordError(!validatePassword(event.target.value))
  }

  return (
    <Box m="20px">
      <Header title="Sign In" subtitle="Please enter your credentials" />
    <Box sx={{ alignContent: "center"}}>
      <Typography>
        <LockOutlinedIcon/>
        Sign-in
      </Typography>
      <Box>
        <FormControl sx={{ m: "20px", width: "400px" }} variant="filled">
          <InputLabel htmlFor="filled-adornment-email">Email</InputLabel>
          <FilledInput
            id="filled-adornment-email"
            type="email"
            name="email"
            value={email}
            onChange={handleEmailChange}
            error={emailError}
            required
          />
        </FormControl>
      </Box>
      <Box>

      <FormControl sx={{ m: "20px", width: "400px" }} variant="filled">
        <InputLabel htmlFor="filled-adornment-password">Password</InputLabel>
        <FilledInput
          id="filled-adornment-password"
          type={showPassword ? "text" : "password"}
          name="password"
          value={password}
          onChange={handlePasswordChange}
          error={passwordError}
          required
          inputProps={{
            pattern: "^(?=.*[A-Z])(?=.*\\d).{6,}$",
            title:
              "Password should have minimum 6 characters and 1 capital letter",
          }}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={handleClickShowPassword}
                onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                {showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>
      </Box>

      <FormControlLabel
        sx={{ m: "20px" }}
        control={
          <Checkbox
            checked={rememberMe}
            onChange={handleRememberMeChange}
            color="primary"
          />
        }
        label="Remember me"
      />

      <Typography variant="body2" sx={{ m: "20px" }}>
        <Link to="/register">Don't have an account? Register here</Link>
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{
          width: "400px",
          backgroundColor: colors.blueAccent[700],
          m: "20px",
        }}
        onClick={handleLogin}
      >
        Login
      </Button>
      </Box>
    </Box>
  )
}

export default Login
